import { useEffect } from "react";
import { useState } from "react";
import "../styles/main.css"
import { useNavigate } from "react-router-dom";
import Detail from "./detail";


const Home = () => {

    let [name,setName]=useState("")
    let [age,setAge]=useState("")
    let[location,setLocation]=useState("")



let handleClick=(e)=>{
    e.preventDefault();

    let data = {name,age,location}

    fetch(" http://localhost:3000/data",{
        method:"POST",
        headers:{"Content-type":"application/json"},
        body: JSON.stringify(data)
    })
alert("added")
}



    return ( 
        <div className="home">
            <h1>CURD</h1>
            <div className="form" >
                <table border="2" bgcolor="gray">
                    <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Location</th>
                        <th>Action</th>
                    </tr>
                    <tr>
                        <td><input type="text" value={name} placeholder="name" required onChange={(e)=>setName(e.target.value)}/></td>
                        <td><input type="text" value={age} onChange={(e)=>setAge(e.target.value)}/></td>
                        <td><input type="text" value={location} onChange={(e)=>setLocation(e.target.value)} /></td>
                        <td><button style={{backgroundColor:"gold",borderRadius:"10px"}} onClick={handleClick}>Add</button></td>
                    </tr>
                </table>
                <br /><br /><br />
            </div>
           
        </div>
     );
}
 
export default Home;
