//     })
//     alert(`${name} deleted`)
// }
let handleDel = (id,name)=>{   
    fetch(`http://localhost:3000/data/${id}`,{
        method:"DELETE"
    })
    alert(`${name} will be deleted permently`)

} 
    return ( 
        <div className="home">
            <h1>CURD</h1>
            <div className="form" >
                <table border="2" bgcolor="gray">
                    <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Location</th>
                        <th>Action</th>
                    </tr>
                    <tr>
                        <td><input type="text" value={name} required onChange={(e)=>setName(e.target.value)}/></td>
                        <td><input type="text" value={age} onChange={(e)=>setAge(e.target.value)}/></td>
                        <td><input type="text" value={location} onChange={(e)=>setLocation(e.target.value)} /></td>
                        <td><button style={{backgroundColor:"gold",borderRadius:"10px"}} onClick={handleClick}>Add</button></td>
                    </tr>
                </table>
                <br /><br /><br />
                {/* <div className="name">
