import { useEffect, useState } from "react";
import { Link,  useNavigate } from "react-router-dom";

const Edit = () => {


    useEffect((id)=>{
        fetch(` http://localhost:3000/data/${id}`).then((res)=>{
            return res.json();
        }).then((resp)=>{
            setName(resp.name);
            setAge(resp.age);
            setLocation(resp.location)
        }).catch((err)=>{
            console.log(err.message);
        })
    },[])


    let [name,setName]=useState("")
    let [age,setAge]=useState("")
    let[location,setLocation]=useState("")
    let[id,setId]=useState("")
    const[active,activeChange]=useState("")
    const[validation,valChange]=useState("")

let navigate = useNavigate()

    let handleClick=(e)=>{
        e.preventDefault();
    
        let data = {id,name,age,location}
    
        fetch(` http://localhost:3000/data/${id}`,{
            method:"PUT",
            headers:{"Content-type":"application/json"},
            body: JSON.stringify(data)
        }).then((res)=>{
            alert("saved successfully..")
            navigate("/")
        }).catch((err)=>{
            console.log(err.message);
        })
    
    }

    return ( 
        <div className="edit">
            <h1>Editing</h1>
            <div className="form">
                <form action="" onSubmit={handleClick}>
                    <div className="id">
                        <label htmlFor="">ID:</label>
                        <input value={id} disabled="disabled"  />
                    </div>
                    <div className="name">
                        <label htmlFor=""></label>
                    <label htmlFor="">Name:</label>
                    <input  value={name} onMouseDown={e=>valChange(true)} required onChange={(e)=>setName(e.target.value)}/>
                    {name.length==0 && validation && <span>Enter the name</span>}
                </div>
                <div className="age">
                    <label htmlFor="">Age:</label>
                    <input  value={age} onChange={(e)=>setAge(e.target.value)}/>
                </div>
                <div className="loc">
                    <label htmlFor="">Location</label>
                    <input  value={location} onChange={(e)=>setLocation(e.target.value)} />
                </div>

                <div className="act">
                    <input checked={active} onChange={e=>activeChange(e.target.checked)} type="checkbox" />
                    <label htmlFor="">Is active</label>
                </div>
                <button>Save</button>
                <Link to="/">Back</Link>

                </form>
            </div>

        </div>
     );
}
 
export default Edit;
