import { useEffect } from "react";
import { useState } from "react";
import "../styles/main.css"

const Home = () => {

    let [name,setName]=useState("")
    let [age,setAge]=useState("")
    let[location,setLocation]=useState("")

let handleClick=(e)=>{
    e.preventDefault();

    let data = {name,age,location}

    fetch(" http://localhost:3000/data",{
        method:"POST",
        headers:{"Content-type":"application/json"},
        body: JSON.stringify(data)
    })
alert("added")
}

let [data,setData]=useState("")

useEffect(()=>{
    let fetching = async()=>{
        let response = await fetch("http://localhost:3000/data")
        let db = await response.json()
        setData(db)
    }
    fetching()
})

    return ( 
        <div className="home">
            <h1>CURD</h1>
            <div className="form" >
                <table border="2" bgcolor="gold">
                    <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Location</th>
                        <th>Action</th>
                    </tr>
                    <tr>
                        <td><input type="text" value={name} required onChange={(e)=>setName(e.target.value)}/></td>
                        <td><input type="text" value={age} onChange={(e)=>setAge(e.target.value)}/></td>
                        <td><input type="text" value={location} onChange={(e)=>setLocation(e.target.value)} /></td>
                        <td><button onClick={handleClick}>Add</button></td>
                    </tr>
                </table>
                <br /><br /><br />
                {/* <div className="name">
                    <label htmlFor="">Name:</label>
                    <input type="text" value={name} required onChange={(e)=>setName(e.target.value)}/>
                </div>
                <div className="age">
                    <label htmlFor="">Age:</label>
                    <input type="text" value={age} onChange={(e)=>setAge(e.target.value)}/>
                </div>
                <div className="loc">
                    <label htmlFor="">Location</label>
                    <input type="text" value={location} onChange={(e)=>setLocation(e.target.value)} />
                </div>
                <button onClick={handleClick}>Add</button> */}
            </div>
            <div className="table">
                    
                <table border="2" bgcolor="green" >
                    <tr>
                        <th width="200">SL.NO</th>
                        <th width="400">NAME</th>
                        <th width="400">AGE</th>
                        <th width="400">LOCATION</th>
                        <th width="110">ACTION</th>
                    </tr>
                </table>
                {data.map(dat=>(
                    <div className="dat">
                        <table border="2"  bgcolor="lightgreen">
                    <tr>
                        <td width="200">{dat.id}</td>
                        <td width="400">{dat.name}</td>
                        <td width="400">{dat.age}</td>
                        <td width="400">{dat.location} </td>
                        <td width="110"><button>Edit</button><button>Delete</button></td>
                   </tr>
                </table>
                    </div>
                ))}
                
            </div>
        </div>
     );
}
 
export default Home;
