const Detail = () => {
    let [data,setData]=useState("")

useEffect(()=>{
    let fetching = async()=>{
        let response = await fetch("http://localhost:3000/data")
        let db = await response.json()
        setData(db)
    }
    fetching()
},[data])

let handleDel = (id,name)=>{   
    fetch(`http://localhost:3000/data/${id}`,{
        method:"DELETE"
    })
    alert(`${name} will be deleted permently`)
} 

let navigate=useNavigate();
let handleEdit = (id)=>{
    navigate(`/edit/${id}`)
}
    return ( 
        <div className="detail">
             <div className="table">
                    
                    <table border="2" bgcolor="gold" >
                        <tr>
                            <th width="200">SL.NO</th>
                            <th width="400">NAME</th>
                            <th width="400">AGE</th>
                            <th width="400">LOCATION</th>
                            <th width="110">ACTION</th>
                        </tr>
                    </table>
                    {data.map(dat=>(
                        <div className="dat">
                            <table border="2"  bgcolor="lightgreen">
                        <tr>
                            <td width="200">{dat.id}</td>
                            <td width="400">{dat.name}</td>
                            <td width="400">{dat.age}</td>
                            <td width="400">{dat.location} </td>
                            <td width="110>"><button onClick={()=>handleEdit(dat.id,dat.name)}>Edit</button>
                            <button onClick={()=>handleDel(dat.id,dat.name)}>Delete</button></td>
                       </tr>
                    </table>
                        </div>
                    ))}
                    
                </div>
        </div>
     );
}
 
export default Detail;
